import{_ as l,c as i,o as e,ag as r}from"./chunks/framework.DzPVt16J.js";const g=JSON.parse('{"title":"项目介绍","description":"","frontmatter":{},"headers":[],"relativePath":"guide/introduction.md","filePath":"guide/introduction.md","lastUpdated":1750784965000}'),t={name:"guide/introduction.md"};function o(n,a,s,h,d,u){return e(),i("div",null,a[0]||(a[0]=[r('<h1 id="项目介绍" tabindex="-1">项目介绍 <a class="header-anchor" href="#项目介绍" aria-label="Permalink to &quot;项目介绍&quot;">​</a></h1><h2 id="项目背景" tabindex="-1">项目背景 <a class="header-anchor" href="#项目背景" aria-label="Permalink to &quot;项目背景&quot;">​</a></h2><p>UINeed Todo AE 是一个专为Adobe After Effects设计的待办事项扩展插件，基于开源项目 <a href="https://github.com/ricocc/uiineed-todo-list" target="_blank" rel="noreferrer">Uiineed Todo List</a> 进行二次开发。</p><h3 id="为什么需要这个扩展" tabindex="-1">为什么需要这个扩展？ <a class="header-anchor" href="#为什么需要这个扩展" aria-label="Permalink to &quot;为什么需要这个扩展？&quot;">​</a></h3><p>在日常的AE工作中，设计师经常需要：</p><ul><li>管理多个项目的任务进度</li><li>跟踪待完成的效果和动画</li><li>记录客户反馈和修改要求</li><li>安排工作优先级</li></ul><p>虽然市面上有很多待办事项应用，但它们往往：</p><ul><li>需要在AE和其他应用之间频繁切换</li><li>缺乏针对视频制作流程的优化</li><li>无法与AE项目直接关联</li><li>界面设计不符合创意工作者的审美需求</li></ul><h2 id="项目目标" tabindex="-1">项目目标 <a class="header-anchor" href="#项目目标" aria-label="Permalink to &quot;项目目标&quot;">​</a></h2><p>UINeed Todo AE 旨在创建一个：</p><h3 id="🎯-专业定制" tabindex="-1">🎯 专业定制 <a class="header-anchor" href="#🎯-专业定制" aria-label="Permalink to &quot;🎯 专业定制&quot;">​</a></h3><ul><li>专为AE设计师量身定制</li><li>针对视频制作流程优化</li><li>与AE工作环境无缝集成</li></ul><h3 id="🎨-美观实用" tabindex="-1">🎨 美观实用 <a class="header-anchor" href="#🎨-美观实用" aria-label="Permalink to &quot;🎨 美观实用&quot;">​</a></h3><ul><li>简洁美观的界面设计</li><li>流畅的交互动效</li><li>符合现代设计趋势</li></ul><h3 id="🔒-安全可靠" tabindex="-1">🔒 安全可靠 <a class="header-anchor" href="#🔒-安全可靠" aria-label="Permalink to &quot;🔒 安全可靠&quot;">​</a></h3><ul><li>本地数据存储</li><li>保护用户隐私</li><li>支持数据备份和迁移</li></ul><h3 id="🚀-高效便捷" tabindex="-1">🚀 高效便捷 <a class="header-anchor" href="#🚀-高效便捷" aria-label="Permalink to &quot;🚀 高效便捷&quot;">​</a></h3><ul><li>快速创建和管理任务</li><li>直观的拖拽排序</li><li>便捷的快捷操作</li></ul><h2 id="技术特色" tabindex="-1">技术特色 <a class="header-anchor" href="#技术特色" aria-label="Permalink to &quot;技术特色&quot;">​</a></h2><h3 id="前端技术" tabindex="-1">前端技术 <a class="header-anchor" href="#前端技术" aria-label="Permalink to &quot;前端技术&quot;">​</a></h3><ul><li><strong>Vue 2.x</strong>：响应式用户界面</li><li><strong>CSS3动画</strong>：流畅的交互效果</li><li><strong>思源黑体</strong>：优化中文显示效果</li></ul><h3 id="后端集成" tabindex="-1">后端集成 <a class="header-anchor" href="#后端集成" aria-label="Permalink to &quot;后端集成&quot;">​</a></h3><ul><li><strong>Adobe CEP</strong>：扩展框架支持</li><li><strong>ExtendScript</strong>：与AE深度集成</li><li><strong>本地存储</strong>：安全的数据管理</li></ul><h3 id="设计理念" tabindex="-1">设计理念 <a class="header-anchor" href="#设计理念" aria-label="Permalink to &quot;设计理念&quot;">​</a></h3><ul><li><strong>极简主义</strong>：去除冗余功能，专注核心需求</li><li><strong>用户体验</strong>：直观的操作流程</li><li><strong>视觉一致性</strong>：与AE界面风格协调</li></ul><h2 id="开源精神" tabindex="-1">开源精神 <a class="header-anchor" href="#开源精神" aria-label="Permalink to &quot;开源精神&quot;">​</a></h2><p>本项目基于MIT许可证开源，我们相信：</p><ul><li><strong>开放协作</strong>：欢迎社区贡献代码和想法</li><li><strong>知识共享</strong>：技术实现完全透明</li><li><strong>持续改进</strong>：根据用户反馈不断优化</li><li><strong>免费使用</strong>：永久免费，无任何限制</li></ul><h2 id="致谢" tabindex="-1">致谢 <a class="header-anchor" href="#致谢" aria-label="Permalink to &quot;致谢&quot;">​</a></h2><p>特别感谢：</p><ul><li><strong><a href="https://www.xiaohongshu.com/user/profile/5f2b6903000000000101f51f" target="_blank" rel="noreferrer">@Rico的设计漫想</a></strong>：原创作者</li><li><strong>Figma社区aakarshna</strong>：Noted设计规范参考</li><li><strong>Adobe</strong>：CEP扩展框架支持</li><li><strong>Vue.js团队</strong>：优秀的前端框架</li></ul><h2 id="下一步" tabindex="-1">下一步 <a class="header-anchor" href="#下一步" aria-label="Permalink to &quot;下一步&quot;">​</a></h2><p>准备好开始使用了吗？查看我们的 <a href="/doc/guide/getting-started">快速开始指南</a> 来安装和配置扩展。</p><p>如果您想了解更多功能特性，请访问 <a href="/doc/features/">功能特性</a> 页面。</p>',34)]))}const b=l(t,[["render",o]]);export{g as __pageData,b as default};
